get_qid_from_tag.map:
function (doc, meta) {
  for(tag in doc.content.tags) {
    emit(doc.content.tags[tag], null);
  }
}

get_ans_by_userid.map:
function (doc, meta) {
  if(doc.answers) {
    for(i=0;i<doc.answers.length;i++) {
      emit(doc.answers[i].poster, [doc.qid, doc.votes, doc.acount, doc.answers[i].aid, doc.title, doc.content.url, doc.views], null);
    }
  }
}

get_acount.map:
function (doc, meta) {
      if(doc.acount) {
              emit(doc.acount, doc.acount, null);
                }
}

get_questions_by_tag.map:
function (doc, meta) {
    if(doc.content.tags) {
        for(i=0; i<doc.content.tags.length; i++) {
            emit(doc.content.tags[i], null);
        }
    }
}

get_questions.map:
function (doc, meta) {
  if(doc.updated) {
    if(doc.sticky && doc.sticky == true)
      emit([doc.sticky,doc.updated], null);
  }

  if(doc.updated && !doc.sticky) {
    emit(doc.updated, null);
  }
}

get_polls.map:
function (doc, meta) {
  if(doc.content.sc || soc.content.mc) {
    emit(meta.id, null);
  }
}
